import { request, gql } from "graphql-request";

// --- Constants ----------------------------------------------------------------------------------
const GRAPH_QL_URL = "http://localhost:4000/graphql";

// --- Comments -----------------------------------------------------------------------------------
async function getComments() {
  const query = gql`
    {
      comments {
        id,
        content
      }
    }
  `;

  const data = await request(GRAPH_QL_URL, query);

  return data.comments;
}

async function createComment(content) {
  const query = gql`
    mutation ($content: String!) {
      create_comment(content: $content) {
        id,
        content
      }
    }
  `;

  const variables = { content };

  const data = await request(GRAPH_QL_URL, query, variables);

  return data.create_comment;
}

export {
  getComments, createComment
}


// GraphQL query to fetch users
async function getUsers() {
  const GET_USERS = gql`
    query {
      users {
        id
        username
        blocked
      }
    }
  `;
  
}

// GraphQL mutation to block/unblock a user
async function blockUnblockUser(userId, block) {
  const BLOCK_UNBLOCK_USER = gql`
    mutation BlockUnblockUser($userId: ID!, $block: Boolean!) {
      blockUnblockUser(userId: $userId, block: $block) {
        id
        username
        blocked
      }
    }
  `;
}


export {
  getUsers, blockUnblockUser
}
